('require_terms', '1'),
('cart_terms_text', '<a href="http://www.townwizard.com/business-license-agreement/" target="_blank">Click Here to View Business License Agreement</a> <br><br>OR<br><br> <a href="http://www.townwizard.com/professional-license-agreement/" target="_blank">Click Here to View Professional License Agreement</a>'),
