(381, 1445, '_form', '<div class="contactForm form">\n    <div class="col">\n        <p><strong>*First Name</strong><br />\n        [text* first-name class:text]</p>\n\n        <p><strong>*Last Name</strong><br />\n        [text* last-name class:text]</p>\n\n        <p><strong>*Phone Number</strong><br />\n        [text* phone-num class:text]</p>\n\n        <p><strong>*Email Address</strong><br />\n        [email* your-email class:text]</p>\n\n        <p><strong>*Best Time to Contact</strong><br />\n        [text* contact-time class:text]</p>\n    </div>\n    <div class="col">\n        <p><strong>Company Name</strong><br />\n        [text company-name class:text]</p>\n\n        <p><strong>Company Website</strong><br />\n        [text company-website class:text]</p>\n\n        <p><strong>Which town are you interested in?</strong><br />\n        [text requested-town class:text]</p>\n\n        <p><strong>How did you hear about TownWizard?</strong><br />\n        [select how-hear class:text include_blank "Entrepreneur Magazine" "Other magazine/website feature" "Google" "Facebook" "BizBuySell" "BusinessesForSale" "BusinessBroker.net" "BusinessOpportunity.com" "Franchise Opportunities Network" "Franchise Direct" "Franchise Clique" "Franchise Advantage" "Word of Mouth" "Partner Referral" "30A.com" "Forbes.com" "Press Release" "Other"]</p>\n        <p class="address"><strong>Corporate Address:</strong><br />\n            2 Overhill Road<br />\n            Suite 400<br />\n            Scarsdale, NY 10583\n        </p>\n    </div>\n    <div class="col txt">\n        <p><strong>Additional Comments or Questions:</strong><br />\n        [textarea your-message]</p>\n        <p><strong>Verify:</strong>[captchac captcha-552 size:m fg:#FF0000] [captchar captcha-552 7/4]<br /><br /></p>\n        <p class="btn"><strong>* Required Field</strong> [submit "Send"]</p>\n    </div>\n</div>'),
